mapper.insert:
		INSERT
			INTO BID_USER A
		(
		  A.USER_ID
		, A.USER_PWD
		, A.USER_NAME
		, A.USER_AGE
        , A.USER_GENDER
        , A.USER_EMAIL
		, A.USER_ADDRESS
		, A.USER_PHONE
		)
		VALUES
		(
		  #{ userID }
		, #{ userPWD }
		, #{ userName }
		, #{ userAge }
		, #{ userGender }
		, #{ userEmail }
		, #{ userAddress }
		, #{ userPhone }
		)

mapper.update:
UPDATE
	BID_USER A
SET 
  A.USER_NAME = #{ userName }
, A.USER_PWD = #{ userPWD }
, A.USER_EMAIL = #{ userEmail }
, A.USER_PHONE = #{ userPhone }
, A.USER_ADDRESS = #{ userAddress }
WHERE A.USER_ID = #{ userID}

mapper.delete:
DELETE
FROM BID_USER A
WHERE A.USER_ID = #{ userID }

mapper.select:
SELECT A.P_ID, A.P_NAME, A.P_SIZE, A.P_GENDER, A.P_PRICE FROM BID_PRODUCT A
A.P_NAME LIKE '%' || #{ nameValue } || '%'
AND A.P_SIZE = #{ sizeValue }

mapper.delete:


DELETE
FROM BID_PRODUCT A
WHERE A.P_ID = #{pId}
